import { Dialog, DialogContent, DialogTitle, DialogContentText, TextField } from "@mui/material";
import React, { useState } from "react";
import { useAppState } from "../../context/AppContext";
import { updateTask } from "../../services/tasks";
import { EditCardProps } from "./EditCardProps";

export const EditCard = (props: EditCardProps) => {
  const { note, priority, id, handleClose, open } = props;

  const [task, setTask] = useState(() => ({
    id: id,
    note: note,
    priority: priority,
  }));
  const { requestTasks } = useAppState();

  const onTextfieldChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTask({ ...task, [event.target.id]: event.target.value });
  };

  const saveData = () => {
    updateTask(task)
      .then(() => {
        handleClose();
        requestTasks();
      })
      .catch((e) => console.log("error", e));
  };

  return (
    <Dialog open={open}
        onClose={props.handleClose}
    >
    <DialogTitle>
        EditCard
    </DialogTitle>
    <DialogContent>
        <DialogContentText>
            <TextField value={task.note} />
            <TextField value={task.priority} />
        </DialogContentText>
    </DialogContent>
    </Dialog>
  );
};

export default EditCard;
