import { useState } from 'react'
import { useAppState } from '../../context/AppContext'

export default function CreateTask() {
    const [task, setTask] = useState({
        'note': '',
        'priority': 1,
    })
    const {requestTasks} = useAppState();

    const onTextAreaChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setTask({
            ...task, [event.target.id]:[event.target.value]
        })
        console.log(task)
    }

  return (
    <div>
        <h3>Create Task</h3>
        <label>Note</label>
        <textarea id="note" value={task.note} onChange={onTextAreaChange}></textarea>
        {task.note}
        <label>Priority</label>
        <textarea id="priority" value={task.priority} onChange={onTextAreaChange} />
    </div>
  )
}
