import React, { useState, useEffect } from "react";
import {Task} from "./components/Models/Tasks"
import {AppContext} from "./context/AppContext";
import { getTasks } from "./services/tasks";
import './App.css';
import { Card } from "./components/Card/Card";
import { CreateTask } from "./components/CreateTask/CreateTask";
import { Button } from "@mui/material";
import { EditCard } from "./components/EditCard/EditCard";

function App() {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [openModal, setOpenModal] = useState<boolean>(false);

  const populateTaskList = (task: [Task]) => {
    setTasks(task);
  };
  useEffect(() => {
    requestTasks();
  }, []);

  const requestTasks = () => {
    getTasks()
      .then((res) => {
        console.log(res.data);
        populateTaskList(res.data);
      })
      .catch((e) => console.log("error", {e}));
  };
  const handleCloseModal = () => {
    setOpenModal(false);
  };

  const renderTask = () => {
    return tasks.map((task: Task, id: number) =>(
      <Card
      task ={{note: task.note, id: task.id, priority: task.priority}}
      tasks={tasks}
      key={id}
      />
      ))
  }
  return (
    <AppContext.Provider
    value={{
      tasks,
      setTasks,
      requestTasks,
      openModal,
      setOpenModal,
    }}
  >
    <>
        <div className="container p-4">
          <Button
            onClick={() => setOpenModal(true)}
          >
            New Task
          </Button>
          {renderTask()}
        </div>

        <CreateModal open={openModal} handleClose={handleCloseModal} />
      </>
    </AppContext.Provider>
  );
}

export default App;
