import React, { useState, useEffect } from "react";
import {Task} from "./components/Models/Tasks"
import AppContext  from "./context/AppContext";
import { getTasks } from "./services/tasks";
import './App.css';
import Card from "./components/Card/Card";

function App() {
  const [tasks, setTasks] = useState<Task[]>([]);

  const populateTaskList = (task: [Task]) => {
    setTasks(task);
  };
  useEffect(() => {
    requestTasks();
  }, []);

  const requestTasks = () => {
    getTasks()
      .then((res) => {
        console.log(res.data);
        populateTaskList(res.data);
      })
      .catch((e) => console.log("error", {e}));
  };

  const renderTask = () => {
    return tasks.map((task: Task, id: number) =>(
      <Card
      task ={{note: task.note, id: task.id, priority: task.priority}}
      tasks={tasks}
      />
      ))
  }
  return (
    <AppContext.Provider
    value={{
      openModal,
      setOpenModal,
      tasks,
      setTasks,
      requestTasks,
    }}
  >
    <div className="App">
      <div>
          {renderTask()}
      </div>
    </div>
    </AppContext.Provider>
  );
}

export default App;
