import { deleteTask } from "../../services/tasks";
import { Task } from "../Models/Tasks";
import { useAppState } from "../../context/AppContext";
import { Button, FormControl, Paper, TextField } from "@mui/material";
import { useState } from "react";

interface CardProps {
  task: {
    priority: number;
    note: string;
    id: number;
  };
  tasks: Task[];
}

export default function Card(props: CardProps) {
  const [modal, setModal] = useState<boolean>(false);
  const { task } = props;
  const { requestTasks } = useAppState();

  const handleDelete = (taskId: number) => {
    deleteTask(taskId)
      .then(() => {
        requestTasks();
      })
      .catch((e) => console.log("error", e));
  };

  const handleModal = () => {
    console.log(FormControl.disable);
    setModal(!modal);
  };

  return (
    <div>
      <Paper elevation={3}>
        <Button onClick={() => handleModal()}>Edit</Button>
        <Button variant="contained" onClick={() => handleDelete(task.id)}>
          Delete
        </Button>
        <TextField value={task.note} disabled></TextField>
        <TextField value={task.priority} disabled></TextField>
      </Paper>
    </div>
  );
}
