import { useState } from "react";
import { useAppState } from "../../context/AppContext";
import axios from "axios";
import { TextField } from "@mui/material";

export default function CreateTask() {
  const [task, setTask] = useState({
    note: "",
    priority: "",
  });
  const { requestTasks } = useAppState();

  const onTextAreaChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTask({
      ...task,
      [event.target.id]: [event.target.value],
    });
  };

  const saveTask = () => {
    axios
      .post("http://localhost:3006/task", task)
      .then(() => {
        requestTasks();
      })
      .catch((e) => console.log(e));
  };

  return (
    <div>
      <h3>Create Task</h3>
      <label>Note</label>
      <TextFi id="note" onChange={onTextAreaChange}></TextFi>
      {task.note}
      <label>Priority</label>
      <input type="number" id="priority" onChange={onTextAreaChange} />
      {task.priority}
      <button className="btn btn-success" onClick={saveTask}>
        Save
      </button>
    </div>
  );
}
