import { useState } from "react";
import { useAppState } from "../../context/AppContext";
import { CreateTaskProps } from "./CreateTaskProps";
import { createTask } from "../../services/tasks";

export const CreateTask = (props: CreateTaskProps) => {
  const [task, setTask] = useState({
    note: "",
  });
  const { requestTasks } = useAppState();

  const onInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTask({ ...task, note: event.target.value });
  };

  const createNewTask = () => {
    createTask(task)
      .then(() => {
        requestTasks();
      })
      .catch((e) => console.log(e));
  };

  return (
    <div>
      <h3>Create Task</h3>
      <label>Note</label>
      <input id="note" onChange={onInputChange}></input>
      {task.note}
      <button onClick={createNewTask}>Save</button>
    </div>
  );
};
