import { useState } from "react";
import { useAppState } from "../../context/AppContext";

export default function CreateTask() {
  const [task, setTask] = useState({
    note: "",
    priority: "",
  });
  const { requestTasks } = useAppState();

  const onTextAreaChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTask({
      ...task,
      [event.target.id]: [event.target.value],
    });
    console.log(task);
  };

  return (
    <div>
      <h3>Create Task</h3>
      <label>Note</label>
      <input id="note" value={task.note} onChange={onTextAreaChange}></input>
      {task.note}
      <label>Priority</label>
      <input id="priority" value={task.priority} onChange={onTextAreaChange} />
    </div>
  );
}
