import { deleteTask } from "../../services/tasks";
import { Task } from "../Models/Tasks";
import { useAppState } from "../../context/AppContext";
import { Button, FormControl, Paper, TextField } from "@mui/material";
import { useState } from "react";
import { Modal } from '@mui/material';

interface CardProps {
  task: {
    priority: number;
    note: string;
    id: number;
  };
  tasks: Task[];
}

export function Card(props: CardProps) {
    const [openEditModal, setOpenEditModal] = useState<boolean>(false)
  const { task } = props;
  const { requestTasks } = useAppState();

  const handleDelete = (taskId: number) => {
    deleteTask(taskId)
      .then(() => {
        requestTasks();
      })
      .catch((e) => console.log("error", e));
  };

  const closeEditModal = () => {
    setOpenEditModal(false)
}

  return (
    <div>
      <Paper elevation={3}>
        <div className="flex">
        <Button size="small" onClick={() => setOpenEditModal(true)} variant="contained" color="primary">
                                Edit
                            </Button>
          <Button variant="contained" onClick={() => handleDelete(task.id)}>
            Delete
          </Button>
          <Button>Save</Button>
        </div>
        <TextField value={task.note}></TextField>
        <TextField value={task.priority}></TextField>
      </Paper>
    </div>
  );
}
